    def config_gpio(self, GPIO):
        config = configparser.ConfigParser()
        config.read('/home/carlos/plataforma/config.ini')

        if self.checkBox.isChecked():
            largura = self.textEdit.toPlainText()
            tamanho = self.textEdit_3.toPlainText()
            mascara = self.textEdit_2.toPlainText()

            config['GPIO']['check_gpio'] = 'TRUE'
            config['GPIO']['largura'] = largura
            config['GPIO']['tamanho'] = tamanho
            config['GPIO']['mascara'] = mascara
        else:
            config['GPIO']['check_gpio'] = 'FALSE'

        with open('config.ini', 'w') as configfile:
            config.write(configfile)
            
        GPIO.close()

